// Fichero src/components/App.js
import { useState } from 'react';

const App = () => {
  //FUNCIÓN PREVENIR ENVÍO FORMULARIO

  const handleOnsubmit = (ev) => {
    ev.preventDefault();
  };

  return (
    <>
      <h3>Selecciona los ingredientes de la tortilla de patatas</h3>
      <form onSubmit={handleOnsubmit}></form>
      <input type="checkbox" id="macarrones" name="macarrones">
        Macarrones
      </input>
    </>
  );
};

export default App;

// Y un formulario con checkboxes para des/marcar estos ingredientes:
// Macarrones
// Patatas
