// Fichero src/components/App.js
// import { useState } from 'react';

const App = () => {
  //FUNCIÓN PREVENIR ENVÍO FORMULARIO

  const handleOnsubmit = (ev) => {
    ev.preventDefault();
  };

  return (
    <>
      <h3>Selecciona los ingredientes de la tortilla de patatas</h3>
      <form onSubmit={handleOnsubmit}>
        <label htmlFor="macarrones">Macarrones</label>
        <input type="checkbox" id="macarrones" name="macarrones" />

        <label htmlFor="patatas">Patatas</label>
        <input type="checkbox" id="patatas" name="patatas" />

        <label htmlFor="nueces">Nueces</label>
        <input type="checkbox" id="nueces" name="nueces" />

        <label htmlFor="huevos">Huevos</label>
        <input type="checkbox" id="huevos" name="huevos" />

        <label htmlFor="cebolla">Cebolla</label>
        <input type="checkbox" id="cebolla" name="cebolla" />

        <label htmlFor="cerveza">Cerveza</label>
        <input type="checkbox" id="cerveza" name="cerveza" />
      </form>
    </>
  );
};

export default App;

// Y un formulario con checkboxes para des/marcar estos ingredientes:
// Macarrones
// Patatas
// Nueces
// Huevos
// Cebolla
// Cerveza
