import { useState } from 'react';

const App = () => {
  const [number, setNumber] = useState(0);

  //Funci贸n que suma 1

  const handleNumber = () => {
    setNumber(number + 1); //setNumber renderiza todo el rato, le estoy diciendo pinta el valor de number y sumale 1.
  };

  //Funci贸n que resetea

  const resetButton = () => {
    const resetNumber = '0';
    setNumber(resetNumber);
  };

  return (
    <div>
      <p> Contador: {number} </p>
      <button type="button" onClick={handleNumber}>
        Sumar 1
      </button>
      <button type="button" onClick={resetButton}>
        Resetear
      </button>
    </div>
  );
};

export default App;
