import '../styles/App.scss';
import { useState } from 'react';

const App = () => {
  const [number, setNumber] = useState(0);

  const handleNumber = (ev) => {
    const changeNumber = '1';
    setNumber(changeNumber);
  };

  const resetButton = (ev) => {
    const resetNumber = '0';
    setNumber(resetNumber);
  };

  const plusButton = (ev) => {
    const plus = <parseInt>number + 1</parseInt>;
    setNumber(plus);
  };

  return (
    <div>
      <p> Contador: {number} </p>
      <button type="button" onClick={handleNumber}>
        Cambiar a 1
      </button>
      <button type="button" onClick={resetButton}>
        Resetear
      </button>
      <button type="button" onClick={plusButton}>
        Sumar 1
      </button>
    </div>
  );
};

export default App;

