import { useState } from 'react';
import '../styles/App.scss';

const App = () => {
  const [tortilla, setTortilla] = useState(true);

  const handleTortilla = (ev) => {
    const click = ev.target.checked;
    setTortilla(click);
  };

  const frase = () => {
    if (tortilla === true) {
      <p>Eres una persona concebollista</p>;
    } else {
      <p>Eres un robot!</p>;
    }
  };
  //FUNCIÓN PREVENIR ENVÍO FORMULARIO

  const handleOnsubmit = (ev) => {
    ev.preventDefault();
  };

  return (
    <>
      <h3>Selecciona los ingredientes de la tortilla de patatas</h3>
      <form onSubmit={handleOnsubmit} className="form">
        <label htmlFor="macarrones">Macarrones</label>
        <input
          type="checkbox"
          id="macarrones"
          name="tortilla"
          onChange={handleTortilla}
        />

        <label htmlFor="patatas">Patatas</label>
        <input
          type="checkbox"
          id="tortilla"
          name="tortilla"
          onChange={handleTortilla}
        />

        <label htmlFor="nueces">Nueces</label>
        <input
          type="checkbox"
          id="nueces"
          name="tortilla"
          onChange={handleTortilla}
        />

        <label htmlFor="huevos">Huevos</label>
        <input
          type="checkbox"
          id="tortilla"
          name="tortilla"
          onChange={handleTortilla}
        />

        <label htmlFor="cebolla">Cebolla</label>
        <input
          type="checkbox"
          id="tortilla"
          name="tortilla"
          onChange={handleTortilla}
        />

        <label htmlFor="cerveza">Cerveza</label>
        <input
          type="checkbox"
          id="cerveza"
          name="tortilla"
          onChange={handleTortilla}
        />
      </form>
    </>
  );
};

export default App;
