import { useState } from 'react';
import '../styles/App.scss';

const App = () => {
  const [tortilla, setTortilla] = useState(false);

  const handleTortilla = (ev) => {
    setTortilla(ev.target.id);
  };

  //FUNCIÓN PREVENIR ENVÍO FORMULARIO

  const handleOnsubmit = (ev) => {
    ev.preventDefault();
  };

  return (
    <>
      <h3>Selecciona los ingredientes de la tortilla de patatas</h3>
      <form onSubmit={handleOnsubmit} className="form">
        <label htmlFor="macarrones">Macarrones</label>
        <input
          type="checkbox"
          id="macarrones"
          name="tortilla"
          onChange={handleTortilla}
        />

        <label htmlFor="patatas">Patatas</label>
        <input
          type="checkbox"
          id="patatas"
          name="tortilla"
          onChange={handleTortilla}
        />

        <label htmlFor="nueces">Nueces</label>
        <input
          type="checkbox"
          id="nueces"
          name="tortilla"
          onChange={handleTortilla}
        />

        <label htmlFor="huevos">Huevos</label>
        <input
          type="checkbox"
          id="huevos"
          name="tortilla"
          onChange={handleTortilla}
        />

        <label htmlFor="cebolla">Cebolla</label>
        <input
          type="checkbox"
          id="cebolla"
          name="tortilla"
          onChange={handleTortilla}
        />

        <label htmlFor="cerveza">Cerveza</label>
        <input
          type="checkbox"
          id="cerveza"
          name="tortilla"
          onChange={handleTortilla}
        />
        <p>
          {tortilla === huevos && cebolla && patatas
            ? 'Eres una persona que le gustan las tortillas'
            : 'eres un robot!'}
        </p>
      </form>
    </>
  );
};

export default App;
