import '../styles/App.scss';
import { useState } from 'react';

function App() {
  //CONST DATA, CON OBJETO DENTRO, QUE GUARDA EL VALOR DE LOS INPUTS
  const [data, setData] = useState({
    title: '',
    synopsis: '',
    language: '',
    age: '',
  });

  //INPUT TITLE

  const [title, setTitle] = useState(''); //Estado de la variable
  const handleInputTitle = (ev) => {
    //Funcion manejadora
    const newValue = ev.target.value; //Cogemos valor del input, lo guardamos en una variable
    setTitle(newValue); //Decimos a setTitle que va a tener el valor de newValue.
  };

  //TEXTAREA

  const [synopsis, setSynopsis] = useState('');
  const handleSynopsis = (ev) => {
    const textValue = ev.current.target;
    setSynopsis(textValue);
  };

  return (
    <div className="App page">
      <header className="header">
        <h1>Añadir películas</h1>
      </header>
      <main>
        <form action="" className="form">
          <div className="col2 mb-1">
            <label className="form__label" htmlFor="title">
              Título:
            </label>

            {/* INPUT TITLE */}

            <input
              type="text"
              placeholder="Ej: Die hard"
              className="form__input-text"
              name="title"
              id="title"
              value={title} //El value tiene como valor la variable de estado, declarada arriba
              onChange={handleInputTitle} //Declaramos evento onChange con función manejadora.
            />
          </div>

          {/* //TEXTAREA */}

          <div className="col2 mb-1">
            <label htmlFor="synopsis">Sinopsis:</label>
            <textarea
              placeholder="Érase una vez..."
              name="synopsis"
              id="synopsis"
              value={synopsis}
              onChange={handleSynopsis}
            ></textarea>
          </div>

          <div className="col2 mb-1">
            <label htmlFor="language">Idioma:</label>
            <select name="language" id="language">
              <option disabled value="">
                Escoge una opción
              </option>
              <option value="es">Español</option>
              <option value="cat">Catalán</option>
              <option value="gal">Gallego</option>
              <option value="eus">Euskera</option>
            </select>
          </div>
          <div className="col2">
            <p>Clasificación por edad</p>
            <div>
              <label htmlFor="age-all">
                <input type="radio" name="age" id="age-all" />
                Todas las edades
              </label>
              <label htmlFor="age-7">
                <input type="radio" name="age" id="age-7" />
                +7 años
              </label>
            </div>
          </div>
          <button>Enviar</button>
          <button>Borrar</button>
        </form>
        <p>{`Título de la película ${title}`}</p>
      </main>
    </div>
  );
}

export default App;
